(2, 1, 1, 'Trĩ ngoại', '1498466577.png', 'tri-ngoai', NULL, NULL, NULL, '{\"meta\":\"B\\u1ec7nh tr\\u0129 c\\u00f3 truy\\u1ec1n nhi\\u1ec5m kh\\u00f4ng? ;  B\\u1ec7nh tr\\u0129 c\\u00f3 th\\u1ec3 t\\u1ef1 kh\\u1ecfi kh\\u00f4ng? ;   H\\u1eadu qu\\u1ea3 kh\\u00f4ng ch\\u1eefa tr\\u1ecb b\\u1ec7nh tr\\u0129   ; Ph\\u1eabu thu\\u1eadt tr\\u0129 sau bao nhi\\u00eau ng\\u00e0y h\\u1ed3i ph\\u1ee5c? ; Ph\\u1eabu thu\\u1eadt tr\\u0129 c\\u00f3 c\\u1ea7n n\\u1eb1m vi\\u1ec7n kh\\u00f4ng? ; Ph\\u00ed ph\\u1eabu thu\\u1eadt tr\\u0129 cao kh\\u00f4ng?\"}', '2017-06-15 01:28:16', '2017-06-26 08:42:57'),
(3, 1, 1, 'Trĩ nội', '1498466586.png', 'tri-noi', NULL, NULL, NULL, '{\"meta\":\"B\\u1ec7nh tr\\u0129 c\\u00f3 truy\\u1ec1n nhi\\u1ec5m kh\\u00f4ng? ;  B\\u1ec7nh tr\\u0129 c\\u00f3 th\\u1ec3 t\\u1ef1 kh\\u1ecfi kh\\u00f4ng? ;   H\\u1eadu qu\\u1ea3 kh\\u00f4ng ch\\u1eefa tr\\u1ecb b\\u1ec7nh tr\\u0129   ; Ph\\u1eabu thu\\u1eadt tr\\u0129 sau bao nhi\\u00eau ng\\u00e0y h\\u1ed3i ph\\u1ee5c? ; Ph\\u1eabu thu\\u1eadt tr\\u0129 c\\u00f3 c\\u1ea7n n\\u1eb1m vi\\u1ec7n kh\\u00f4ng? ; Ph\\u00ed ph\\u1eabu thu\\u1eadt tr\\u0129 cao kh\\u00f4ng?\"}', '2017-06-15 01:28:23', '2017-06-26 08:43:06'),
(4, 1, 1, 'Trĩ hỗn hợp', '1498466599.png', 'tri-hon-hop', NULL, NULL, NULL, '{\"meta\":\"B\\u1ec7nh tr\\u0129 c\\u00f3 truy\\u1ec1n nhi\\u1ec5m kh\\u00f4ng? ;  B\\u1ec7nh tr\\u0129 c\\u00f3 th\\u1ec3 t\\u1ef1 kh\\u1ecfi kh\\u00f4ng? ;   H\\u1eadu qu\\u1ea3 kh\\u00f4ng ch\\u1eefa tr\\u1ecb b\\u1ec7nh tr\\u0129   ; Ph\\u1eabu thu\\u1eadt tr\\u0129 sau bao nhi\\u00eau ng\\u00e0y h\\u1ed3i ph\\u1ee5c? ; Ph\\u1eabu thu\\u1eadt tr\\u0129 c\\u00f3 c\\u1ea7n n\\u1eb1m vi\\u1ec7n kh\\u00f4ng? ; Ph\\u00ed ph\\u1eabu thu\\u1eadt tr\\u0129 cao kh\\u00f4ng?\"}', '2017-06-15 01:28:38', '2017-06-26 08:43:19'),
