(6, 1, 5, 'Áp xe hậu môn', '1498466613.png', 'ap-xe-hau-mon', NULL, NULL, NULL, '{\"meta\":\"Chi ph\\u00ed ph\\u1eabu thu\\u1eadt \\u00e1p xe h\\u1eadu m\\u00f4n? ; Ph\\u1eabu thu\\u1eadt \\u00e1p xe h\\u1eadu m\\u00f4n \\u0111au kh\\u00f4ng? ; \\u00c1p xe h\\u1eadu m\\u00f4n b\\u1eaft bu\\u1ed9c ph\\u1ea3i l\\u00e0m ph\\u1eabu thu\\u1eadt kh\\u00f4ng? ; Ph\\u1eabu thu\\u1eadt \\u00e1p xe h\\u1eadu m\\u00f4n c\\u00f3 c\\u1ea7n n\\u1eb1m vi\\u1ec7n kh\\u00f4ng?; \\u00c1p xe h\\u1eadu m\\u00f4n c\\u00f3 t\\u1ef1 kh\\u1ecfi kh\\u00f4ng? ; \\u00c1p xe h\\u1eadu m\\u00f4n c\\u00f3 d\\u1ec5 ch\\u1eefa kh\\u00f4ng?\"}', '2017-06-15 01:28:57', '2017-06-26 08:43:33'),
(7, 1, 5, 'Ngứa rát hậu môn', '1498466632.png', 'ngua-rat-hau-mon', NULL, NULL, NULL, '{\"meta\":\"Ng\\u1ee9a h\\u1eadu m\\u00f4n, ra m\\u00e1u? ; Nguy\\u00ean nh\\u00e2n \\u0111au ng\\u1ee9a h\\u1eadu m\\u00f4n? ; Ng\\u1ee9a h\\u1eadu m\\u00f4n, n\\u1ed5i m\\u1ee5n th\\u1ecbt?  ;T\\u1ea1i sao h\\u1eadu m\\u00f4n \\u1ea9m \\u01b0\\u1edbt v\\u00e0 ng\\u1ee9a? ; Ng\\u1ee9a h\\u1eadu m\\u00f4n l\\u00e0 b\\u1ec7nh g\\u00ec? ; Ng\\u1ee9a h\\u1eadu m\\u00f4n c\\u00f3 th\\u1ec3 ch\\u1eefa kh\\u00f4ng?\"}', '2017-06-15 01:29:05', '2017-06-26 08:43:52'),
(8, 1, 5, 'Polyp hậu môn', '1498466642.png', 'polyp-hau-mon', NULL, NULL, NULL, '{\"meta\":\"L\\u00e0m th\\u1ebf n\\u00e0o \\u0111\\u1ec3 ph\\u00e2n bi\\u1ec7t Polyp h\\u1eadu m\\u00f4n v\\u1edbi b\\u1ec7nh tr\\u0129? ; Chi ph\\u00ed c\\u1eaft b\\u1ecf Polyp h\\u1eadu m\\u00f4n  ;  Polyp h\\u1eadu m\\u00f4n ra m\\u00e1u ph\\u1ea3i l\\u00e0m th\\u1ebf n\\u00e0o? ;   Polyp h\\u1eadu m\\u00f4n ph\\u1ea3i ch\\u1eefa kh\\u00f4ng?  ;  Polyp h\\u1eadu m\\u00f4n ng\\u1ee9a th\\u00ec ph\\u1ea3i l\\u00e0m th\\u1ebf n\\u00e0o? ;   Polyp h\\u1eadu m\\u00f4n c\\u00f3 nh\\u1eefng nguy hi\\u1ec3m g\\u00ec?\"}', '2017-06-15 01:29:14', '2017-06-26 08:44:02'),
(9, 1, 5, 'Đau hậu môn', NULL, 'dau-hau-mon', NULL, NULL, NULL, '{\"meta\":\"\\u0110au h\\u1eadu m\\u00f4n? Ra m\\u00e1u?;  \\u0110au h\\u1eadu m\\u00f4n, H\\u1eadu m\\u00f4n m\\u1ecdc m\\u1ee5n th\\u1ecbt?;  T\\u1ea1i sao l\\u1ea1i \\u0111au h\\u1eadu m\\u00f4n? ; T\\u1ea1i sao h\\u1eadu m\\u00f4n v\\u1eeba \\u0111au v\\u1eeba ng\\u1ee9a?;  T\\u1ea1i sao \\u0111i ngo\\u00e0i l\\u1ea1i \\u0111au h\\u1eadu m\\u00f4n?  ;Nguy\\u00ean nh\\u00e2n \\u0111au h\\u1eadu m\\u00f4n?\"}', '2017-06-15 01:29:22', '2017-06-15 02:23:26'),
(10, 1, 5, 'Hậu môn có vật thể lạ', '1498467204.png', 'hau-mon-co-vat-the-la', NULL, NULL, NULL, '{\"meta\":\"H\\u1eadu m\\u00f4n m\\u1ecdc th\\u1ecbt tr\\u00ean da? ; H\\u1eadu m\\u00f4n m\\u1ecdc m\\u1ee5n th\\u1ecbt l\\u00e0 tr\\u0129 sao?;  H\\u1eadu m\\u00f4n c\\u00f3 v\\u1eadt th\\u1ec3 l\\u1ea1, ng\\u1ee9a?  ;\\r\\nH\\u1eadu m\\u00f4n c\\u00f3 m\\u1ee5n th\\u1ecbt r\\u01a1i ra?  ;H\\u1eadu m\\u00f4n m\\u1ecdc m\\u1ee5n th\\u1ecbt, ra m\\u00e1u? ; H\\u1eadu m\\u00f4n m\\u1ecdc m\\u1ee5n th\\u1ecbt l\\u00e0 b\\u1ec7nh g\\u00ec?\"}', '2017-06-15 01:29:30', '2017-06-26 08:53:24'),
(11, 1, 0, 'Chấn thương hậu môn', NULL, 'chan-thuong-hau-mon', NULL, NULL, NULL, '[]', '2017-06-15 01:29:40', '2017-06-15 01:29:40'),
(12, 1, 11, 'Rò hậu môn', '1498466702.png', 'ro-hau-mon', NULL, NULL, NULL, '{\"meta\":\"Ph\\u1eabu thu\\u1eadt r\\u00f2 h\\u1eadu m\\u00f4n \\u0111au kh\\u00f4ng? ;  R\\u00f2 h\\u1eadu m\\u00f4n c\\u00f3 t\\u1ef1 kh\\u1ecfi kh\\u00f4ng? ;  Ph\\u1eabu thu\\u1eadt r\\u00f2 h\\u1eadu m\\u00f4n c\\u00f3 c\\u1ea7n n\\u1eb1m vi\\u1ec7n kh\\u00f4ng?  ; R\\u00f2 h\\u1eadu m\\u00f4n c\\u00f3 b\\u1eaft bu\\u1ed9c l\\u00e0m ph\\u1eabu thu\\u1eadt kh\\u00f4ng? ;  Ph\\u1eabu thu\\u1eadt r\\u00f2 h\\u1eadu m\\u00f4n sau bao l\\u00e2u th\\u00ec h\\u1ed3i ph\\u1ee5c? ;  Ph\\u1eabu thu\\u1eadt r\\u00f2 h\\u1eadu m\\u00f4n l\\u00e0m nh\\u01b0 th\\u1ebf n\\u00e0o?\"}', '2017-06-15 01:29:48', '2017-06-26 08:45:02'),
(13, 1, 11, 'Nứt kẽ hậu môn', '1498466716.png', 'nut-ke-hau-mon', NULL, NULL, NULL, '{\"meta\":\"N\\u1ee9t k\\u1ebd h\\u1eadu m\\u00f4n c\\u00f3 th\\u1ec3 t\\u1ef1 kh\\u1ecfi kh\\u00f4ng? ;  N\\u1ee9t k\\u1ebd h\\u1eadu m\\u00f4n c\\u00f3 b\\u1eaft bu\\u1ed9c ph\\u1ea3i l\\u00e0m ph\\u1eabu thu\\u1eadt kh\\u00f4ng?  ;N\\u1ee9t k\\u1ebd h\\u1eadu m\\u00f4n \\u0111i ngo\\u00e0i ra m\\u00e1u ph\\u1ea3i l\\u00e0m th\\u1ebf n\\u00e0o? ; N\\u1ee9t k\\u1ebd h\\u1eadu m\\u00f4n kh\\u00f4ng m\\u1ed5 th\\u00ec ch\\u1eefa tr\\u1ecb b\\u1eb1ng c\\u00e1ch n\\u00e0o? ; Ph\\u1eabu thu\\u1eadt n\\u1ee9t k\\u1ebd h\\u1eadu m\\u00f4n c\\u00f3 c\\u1ea7n n\\u1eb1m vi\\u1ec7n kh\\u00f4ng?;  Ph\\u1eabu thu\\u1eadt n\\u1ee9t k\\u1ebd h\\u1eadu m\\u00f4n c\\u00f3 \\u0111au kh\\u00f4ng?\"}', '2017-06-15 01:29:57', '2017-06-26 08:45:16'),
(14, 1, 11, 'Sa trực tràng', '1498466693.png', 'sa-truc-trang', NULL, NULL, NULL, '{\"meta\":\"L\\u00e0m th\\u1ebf n\\u00e0o \\u0111\\u1ec3 ph\\u00e2n bi\\u1ec7t b\\u1ec7nh tr\\u0129 v\\u00e0 sa tr\\u1ef1c tr\\u00e0ng? ; Ph\\u1eabu thu\\u1eadt sa tr\\u1ef1c tr\\u00e0ng \\u0111au kh\\u00f4ng? ; Ph\\u1eabu thu\\u1eadt sa tr\\u1ef1c tr\\u00e0ng l\\u00e0m nh\\u01b0 th\\u1ebf n\\u00e0o? ; Sa tr\\u1ef1c tr\\u00e0ng c\\u00f3 t\\u1ef1 kh\\u1ecfi kh\\u00f4ng? ; Sa tr\\u1ef1c tr\\u00e0ng c\\u00f3 b\\u1eaft bu\\u1ed9c ph\\u1ea3i l\\u00e0m ph\\u1eabu thu\\u1eadt kh\\u00f4ng? ; Ph\\u1eabu thu\\u1eadt sa tr\\u1ef1c tr\\u00e0ng c\\u00f3 c\\u1ea7n n\\u1eb1m vi\\u1ec7n kh\\u00f4ng?\"}', '2017-06-15 01:30:05', '2017-06-26 08:44:53'),
